import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { Image } from 'antd';

const ImageSliderContainer = styled(({ className, children }) => (
  <div className={className}>{children}</div>
))`
  display: flex;
  width: 600px;
  height: 550px;
`;

const ThumbnailBar = styled(({ className, images, setCurrentImage }) => {
  const focusImage = (imageSrc) => {
    console.log(imageSrc);
    console.log(document.getElementById(imageSrc));
    const element = document.getElementById(imageSrc);
    window.setTimeout(() => element.focus(), 100);

    console.log('eiei');
  };

  return (
    <div className={className}>
      {images
        ? images.map((image) => {
            return (
              <Image
                src={image}
                preview={false}
                onClick={() => {
                  setCurrentImage(image);
                  focusImage(image);
                }}
                id={image}
              />
            );
          })
        : null}
    </div>
  );
})`
  display: flex;
  flex-direction: column;
  width: 100px;
  height: 100%;
  div.ant-image:hover,
  div.ant-image:focus {
    border: 5px solid black;
  }
  div.ant-image {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-top: 20px;
    width: 100px;
    height: 100px;
  }
  img {
    max-width: 100%;
    max-height: 100%;
    width: auto;
    height: auto;
  }
  img:focus {
    min-width: 1000px;
  }
`;

const DisplayedImage = styled(({ className, children }) => (
  <div className={className}>{children}</div>
))`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 425px;
  margin-left: 30px;
  height: 100%;
  border: 1px solid #d7d7d7;
`;

const ImageSlider = ({ images }) => {
  const [currentImage, setCurrentImage] = useState('');

  useEffect(() => {
    if (images instanceof Array) setCurrentImage(images[0]);
  }, [images]);

  return (
    <ImageSliderContainer>
      <ThumbnailBar images={images} setCurrentImage={setCurrentImage} />
      <DisplayedImage>
        <Image
          // src="https://img.freepik.com/free-vector/word-hello-speech-bubble-vector_53876-60258.jpg?size=626&ext=jpg"
          src={currentImage}
          // eslint-disable-next-line max-len
          fallback="data:image/png;base64,"
          // preview={false}
          style={{ width: '400px' }}
        />
      </DisplayedImage>
    </ImageSliderContainer>
  );
};

export default ImageSlider;
